mapper.insert:
INSERT INTO board(
num, userid, title, regdate, content, count, filename, ref
) VALUES(
board_seq.nextval, #{userid}, #{title}, sysdate, #{content}, #{count},
#{filename},board_seq.currval
)

mapper.update:
UPDATE board SET
title = #{title}, filename = #{filename}, content = #{content}
WHERE num = #{num}
